import React from 'react';

class Navbar extends React.Component {
   constructor () {
      super();

      this.state = {
         showMessagePane: false
      }
   }

   toggleMessagePane() {
      if(this.state.showMessagePane === false) {
         this.state.showMessagePane = !this.state.showMessagePane;
      }
   }

   render(){
      return(
         <div class="navbar">
            <button onClick={this.toggleMessagePane} type="button" id="openContactButton"></button>
         </div>
      );
   }
}
export default Navbar;
